import {React, useState} from 'react'
import Header from '../components/Header.jsx'
import LandScreen from '../components/HomePageWelcome.jsx'
import '../styles/HomePage.css'
import blob1 from "../assets/svgs/blob1.svg"
import blob2 from "../assets/svgs/blob2.svg"

function Homepage() {
  const [activeDiv, setActiveDiv] = useState(0);

  const handlePrev = () => {
    setActiveDiv((prevDiv) => (prevDiv - 1 + totalDivs) % totalDivs);
  };

  const handleNext = () => {
    setActiveDiv((prevDiv) => (prevDiv + 1) % totalDivs);
  };

  const totalDivs = 3;
  return (
    <>
    <LandScreen></LandScreen>
    <Header></Header>
    <div className="HomepageBody">
        <img src={blob1} alt="" className='blob1' />
        <div className="homepageContent">
          <div className="gamesList">
            <h1>Our games</h1>
            <div className="gameContainer">
              {/* Render your divs here based on the activeDiv state */}
              {Array.from({ length: totalDivs }, (_, index) => (
                <div
                  key={index}
                  className={`galleryDiv ${index === activeDiv ? 'active' : ''}`}
                >
                  {/* Your div content goes here */}
                  <p>Gallery Item {index + 1}</p>
                </div>
              ))}
            </div>
            <div className="galleryArrows">
              <button onClick={handlePrev}>&lt;</button>
              <button onClick={handleNext}>&gt;</button>
            </div>
          </div>
        </div>
        <img src={blob2} alt="" className='blob2' />
      </div>
    </>
  )
}

export default Homepage
